<?phpuse Illuminate\Database\Seeder;class ClientTableSeeder extends Seeder{    /**     * Run the database seeds.     *     * @return void     */    public function run()    {        $faker = Faker\Factory::create('ar_JO');        foreach (range(1, 50) as $index) {            DB::table('clients')->insert([                'type' => 'renter',                'first_name' => $faker->firstName,                'second_name' => $faker->firstName('male'),                'third_name' => $faker->firstName('male'),                'last_name' => $faker->firstName('male'),                'gender' => $faker->randomElement(['male', 'female']),                'birth_date' => $faker->date,                'id_number' => $faker->randomNumber,                'area_id' => rand(50, 200),                'activation_code' => rand(22222, 99999),                'credit' => rand(1000, 99999),                'email' => $faker->email,                'phone' => $faker->phoneNumber,                'mobile' => $faker->phoneNumber,                'password' => $faker->password,                'address' => $faker->address,                'status' => 'active',                'verified_at' => $faker->date,                'updated_at' => $faker->dateTime,            ]);        }    }}